package com.cts.creatio.crm.language.basics;

public class Assignment1_Datatype {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

		float tempature = 25.5f;
		boolean order = true;
		int phone = 123 - 456 - 7890;
		double money = 1000.50;
		String email = "john.doe@example.com";
		double latitude = 37.7749;
		double longitude = -122.4194;
		boolean marital_status = true;
		String occupation = "Software Engineer";
		String colour = "Blue";
		short year = 2023;
		long followers = 1000000;
		float movieratting = 7.5f;
		char bloodtype = 'A';
		String bookname = "To Kill a Mockingbird";
		double employee = 500;
		String time = "2:30 PM";
		String country = "United States";
		String eye_color = "Brown";
		String birthplace = "New York City";
		double Distance_between_two_cities = 200.5;

		System.out.println("Temperature of a city in degrees Celsius = " + tempature);
		System.out.println("Whether a customer has placed an order = " + order);
		System.out.println("Person's phone number = " + phone);
		System.out.println("Amount of money in a customer's bank account = " + money);
		System.out.println("Person's email address = " + email);
		System.out.println("Coordinates of a location (latitude, longitude) = " + latitude + "," + longitude);
		System.out.println("Person's marital status = " + marital_status);
		System.out.println("Person's occupation = " + occupation);
		System.out.println("Person's favourite colour = " + colour);
		System.out.println("Current year = " + year);
		System.out.println("Number of followers on a social media platform = " + followers);
		System.out.println("Rating of a movie = " + movieratting);
		System.out.println("Person's blood type = " + bloodtype);
		System.out.println("Title of a book = " + bookname);
		System.out.println("Number of employees in a company = " + employee);
		System.out.println("Time of an event = " + time);
		System.out.println("Name of a country = " + country);
		System.out.println("Person's eye color = " + eye_color);
		System.out.println("Person's birthplace = " + birthplace);
		System.out.println("Distance between two cities = " + Distance_between_two_cities);

	}

}
